INSERT INTO CodeRow VALUES(69364,170,25185,'HGustavs',199,'&lt;li&gt;The startup test is an even smaller app for testing if WebGL works on any platform.&lt;li&gt;','main',NULL,'49bad8d7ad3c3a20c1fc093658cb8daa14c7a581');
INSERT INTO CodeRow VALUES(69365,170,25185,'HGustavs',200,'&lt;li&gt;An internet connection is necessary during the benchmark.&lt;li&gt;','main',NULL,'49bad8d7ad3c3a20c1fc093658cb8daa14c7a581');
INSERT INTO CodeRow VALUES(69366,170,25185,'HGustavs',201,'&lt;li&gt;Shader performance may vary, some GPU configurations may execute the stress tests faster than the ordinary benchmark.&lt;li&gt;','main',NULL,'49bad8d7ad3c3a20c1fc093658cb8daa14c7a581');
INSERT INTO CodeRow VALUES(69367,170,25185,'HGustavs',202,'&lt;li&gt;Run the benchmark for at least 1 minute as performance often is slower at first.&lt;li&gt;','main',NULL,'49bad8d7ad3c3a20c1fc093658cb8daa14c7a581');
INSERT INTO CodeRow VALUES(69368,170,25185,'HGustavs',203,'&lt;li&gt;Some plugins may under certain conditions affect WebGL performance. Consider disabling &lt;i&gt;Firebug&lt;i&gt;, &lt;i&gt;Greasemonkey&lt;i&gt; and similar plugins if accurate benchmarking performance is needed.&lt;li&gt;','main',NULL,'49bad8d7ad3c3a20c1fc093658cb8daa14c7a581');
INSERT INTO CodeRow VALUES(69369,170,25185,'HGustavs',204,'&lt;li&gt;Any development computer must be able to run these benchmarks without major visual artifacts&lt;li&gt;','main',NULL,'49bad8d7ad3c3a20c1fc093658cb8daa14c7a581');
INSERT INTO CodeRow VALUES(69370,170,25185,'HGustavs',205,'&lt;li&gt;We consider interactive framerates on the main benchmark to be sufficient for development computers.&lt;li&gt;','main',NULL,'49bad8d7ad3c3a20c1fc093658cb8daa14c7a581');
INSERT INTO CodeRow VALUES(69371,170,25185,'HGustavs',206,'&lt;li&gt;Interactive frame rates on the shader stress test (especially High resolution version) is not necessary for our purpouses.&lt;li&gt;','main',NULL,'49bad8d7ad3c3a20c1fc093658cb8daa14c7a581');
INSERT INTO CodeRow VALUES(69372,170,25185,'HGustavs',207,'&lt;li&gt;If any development computer runs the stress test benchmarks other development computers should be &lt;i&gt;largely unaffected.&lt;i&gt;&lt;li&gt;','main',NULL,'49bad8d7ad3c3a20c1fc093658cb8daa14c7a581');
