INSERT INTO issue VALUES(455,'455','2014-09-21T13:42:12Z',263,14,'HGustavs','Closed','Collapsable dugga-view.','The dugga-view should remember if a dugga and its variants are collapsed in the view or not. By adding an attribute collapsed to dugga we can remember the collapsed state of the dugga-elements for easier editing.');
