INSERT INTO issue VALUES(1813,'1813','2015-11-01T19:09:17Z',669,20,'HGustavs','Closed','Incorrect value for important rows','The error message Incorrect value s  for important rows! is displayed in the code viewer for many files and for unkown reasons. For example putting rows 3-11 in a file with 15 lines does not work.');
INSERT INTO issue VALUES(1814,'1814','2015-11-01T19:24:15Z',669,20,'HGustavs','Closed','Link lists are not populated correctly','The item lists for the preview function in the code viewer does not show web links  which should be the most common use . Furthermore the list has items from other courses also  not just the local course.');
