INSERT INTO event VALUES(109854,'9975','2021-04-12T15:08:04Z',2658,16,'HGustavs','comment','this.linedist = function  xk yk  if this.symbolkind==4  var x1=points[this.topLeft].x; var y1=points[this.topLeft].y; var x2=points[this.bottomRight].x; var y2=points[this.bottomRight].y; var px = x2-x1; var py = y2-y1; var len = px*px + py*py; var u =   xk - x1  * px +  yk - y1  * py  len; if u ** 1  u = 1; else if u __ 0  u = 0; var x = x1 + u * px; var y = y1 + u * py; px = x - xk; py = y - yk; dst = px*px + py*py; return dst; else return -1; ','UNK');
